import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSpinner } from "@fortawesome/free-solid-svg-icons";
import NavBar from "../components/NavBar";
import PlanetList from "../context/PlanetList.js";
import { useEffect, useState } from "react";
//BOOTSTRAP
import { Container, Row, Col, Button } from "react-bootstrap";
const Planets = () => {
  const pList = [
    { Name: null, Population: null, Img: null, Ships: [], key: null },
  ];
  let shipRender = [];
  if (
    localStorage.getItem("allShipList") === [] ||
    localStorage.getItem("allShipList") !== null
  ) {
    shipRender = [...JSON.parse(localStorage.getItem("allShipList"))];
  } else {
    shipRender = [...ShipList];
  }
  const [allPlanets, setAllPlanets] = useState(pList);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    if (allPlanets.length === 1) {
      if (localStorage.getItem("allPlanetsList") == null) {
        setAllPlanets((allPlanets) => {
          return PlanetList;
        });
        setIsLoading((isLoading) => {
          return false;
        });
      } else {
        localStorage.clear();
        setAllPlanets((allPlanets) => {
          const obj = JSON.parse(localStorage.getItem("allPlanetsList"));
          console.log(obj);
          return obj;
        });
        setIsLoading((isLoading) => {
          return false;
        });
      }
    }
  }, [allPlanets]);
  localStorage.setItem("allPlanetsList", JSON.stringify(allPlanets));
  if (isLoading) {
    return (
      <Container>
        <Row className="justify-content-center loading">
          <Col sm="1">
            <FontAwesomeIcon icon={faSpinner} spin />
          </Col>
        </Row>
      </Container>
    );
  } else {
    return (
      <Container fluid>
        <NavBar />

        {allPlanets.map((props) => {
          return (
            <Row className="singlePlanet " key={props.key}>
              <Col sm="2" className="planetPic no-float">
                <img src={props.Img} alt={props.Name} />
                <p>Name: {props.Name}</p>
                <p>Population: {props.Population}</p>
              </Col>
              {props.Ships.map((props2) => {
                return (
                  <Col
                    className="planetShipInfo justify-content-center"
                    key={props2.key}
                  >
                    <div>
                      <img src={props2.Img} alt={props2.Name} />
                      <p>Name: {props2.Name}</p>
                      <p>Passengers: {props2.Population}</p>
                    </div>
                  </Col>
                );
              })}
            </Row>
          );
        })}
      </Container>
    );
  }
};
export default Planets;
