import NavBar from "../components/NavBar";
import { Link } from "react-router-dom";
//BOOTSTRAP
import { Container, Row, Col, Button } from "react-bootstrap";
//ICONS
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHammer,
  faTrashCan,
  faSpinner,
} from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";
import ShipBuilder from "./ShipBuilder";
//need to add in loading functionality
const SpaceCraft = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [isBuilding, setIsBuilding] = useState(false);
  const [ships, setShips] = useState([]);
  useEffect(() => {
    setIsLoading((isLoading) => {
      return false;
    });
  }, []);
  const handleShipInfo = (e) => {
    e.preventDefault();
    if (e.target.classList.contains("delBtn")) {
      console.log("del");
      ships.map((ship) => {
        console.log(ship.name);
        return true;
      });
      console.log(ships);
    } else {
      console.log("not delBtn");
    }
  };
  const handleBuilding = (e) => {
    e.preventDefault();
    setIsBuilding((isBuilding) => {
      return true;
    });
  };
  const addShip = (name, capacity, description, img) => {
    setShips((ships) => [...ships, { name, capacity, description, img }]);
    setIsBuilding((isBuilding) => false);
  };
  console.log(ships);
  if (isLoading) {
    return (
      <Container>
        <NavBar />
        <Row className="justify-content-center loading">
          <Col sm="1">
            <FontAwesomeIcon icon={faSpinner} spin />
          </Col>
        </Row>
      </Container>
    );
  } else if (isBuilding) {
    return <ShipBuilder addShip={addShip} />;
  } else {
    return (
      <Container>
        <NavBar />
        <Row className="firstRow">
          <Col>
            <Button variant="outline-light" onClick={handleBuilding}>
              <FontAwesomeIcon icon={faHammer} /> Build a Ship
            </Button>
          </Col>
        </Row>
        {/* single ship example - going to need to map through array for actual */}
        <Row className="singleShip" onClick={handleShipInfo}>
          <Col sm="2" className="shipPicture">
            <img
              src="https://w7.pngwing.com/pngs/225/630/png-transparent-red-spaceship-rocket-icon-rocket-image-file-formats-cartoon-rocket-launch.png"
              alt="Space Ship Picture"
            />
          </Col>
          <Col sm="8" className="shipInfo">
            <p>Name: ship</p>
            <p>Capasity:1000</p>
          </Col>
          <Col sm="1" className="destroyBtn ">
            <Button
              variant="outline-danger"
              className="delBtn"
              onClick={handleShipInfo}
            >
              Destroy <FontAwesomeIcon icon={faTrashCan} />
            </Button>
          </Col>
        </Row>
      </Container>
    );
  }
};
export default SpaceCraft;
